            else
            {
                pyLessonsData.push(element)
            }
        });
        const lessonNo =parseInt('<?php echo $current_lessonMath?>')
        const lastMathLesson = lessonNo-1
        const nextMathLesson  = lessonNo+1

        document.querySelector(".math .startLessonZero").style.display = "none"
        document.querySelector(".math .finishLesson").style.display = "none"
        document.querySelector(".math .finishSomething").style.display = "none"

        const DOC_lastMath = document.querySelector(".math #LastLessonNumberMath")
        const DOC_currentMath= document.querySelector(".math #CurrentLessonNumberMath")
        const DOC_nextMath = document.querySelector(".math #NextLessonNumberMath")

        const Phone_currentMath= document.querySelector(".math #CurrentLessonNumberMathPhone")

        const DOC_numberLast = document.querySelector(".math .numberLast")
        const DOC_numberCurrent = document.querySelector(".math .numberCurrent")
        const DOC_numberNext = document.querySelector(".math .numberNext")
        
        if(lessonNo>mathLessonsData.length){
            $(".math .startLessonZero").css("display","none")
            $(".math.finishLesson").css("display","none")
            $(".math .last_lesson").css("display","none")
            $(".math .current_lesson").css("display","none")
            $(".math .next_lesson").css("display","none")
            $(".math #line1").css("display","none")
            $(".math #line2").css("display","none")
            $(".math .doPracticeMath").css("display","none")
            $(".math .finishSomething").css("display","flex")
            $(".math #mathPhone").css("display","none")
        }else{
            if((lessonNo-1) === 0){
            document.querySelector(".startLessonZero").style.display = "flex"
            document.querySelector(".last_lesson").style.display = "none"

            DOC_currentMath.textContent = mathLessonsData[lessonNo-1]["lessonContent"]
            DOC_numberCurrent.textContent = mathLessonsData[lessonNo-1]["lessonID"]
            Phone_currentMath.textContent = mathLessonsData[lessonNo-1]["lessonContent"]
            Phone_currentMath.textContent = mathLessonsData[lessonNo-1]["lessonContent"]

            DOC_nextMath.textContent = mathLessonsData[lessonNo]["lessonContent"]
            DOC_numberNext.textContent = mathLessonsData[lessonNo]["lessonID"]
        }else{
            DOC_lastMath.textContent = mathLessonsData[lessonNo-2]["lessonContent"]
            DOC_numberLast.textContent = mathLessonsData[lessonNo-2]["lessonID"]

            DOC_currentMath.textContent = mathLessonsData[lessonNo-1]["lessonContent"]
            DOC_numberCurrent.textContent = mathLessonsData[lessonNo-1]["lessonID"]

            Phone_currentMath.textContent = mathLessonsData[lessonNo-1]["lessonContent"]
            Phone_currentMath.textContent = mathLessonsData[lessonNo-1]["lessonContent"]
            if(mathLessonsData.length == lessonNo){
                document.querySelector(".finishLesson").style.display = "flex"
                document.querySelector(".next_lesson").style.display = "none"
            }else{
                DOC_nextMath.textContent = mathLessonsData[lessonNo]["lessonContent"]
                DOC_numberNext.textContent = mathLessonsData[lessonNo]["lessonID"]
            }
            
        }
        }
        
