        const DOC_lastPy = document.querySelector("#LastLessonNumberPy")
        const DOC_currentPy= document.querySelector(" #CurrentLessonNumberPy")
        const DOC_nextPy = document.querySelector("#NextLessonNumberPy")

        const Phone_currentPy= document.querySelector("#CurrentLessonNumberPyPhone")

        document.querySelector(".python .startLessonZero").style.display = "none"
        document.querySelector(".python .finishLesson").style.display = "none"
        document.querySelector(".python .finishSomething").style.display = "none"

        const DOC_numberLastPy = document.querySelector(".python .numberLast")
        const DOC_numberCurrentPy = document.querySelector(".python .numberCurrent")
        const DOC_numberNextPy = document.querySelector(".python .numberNext")
        
        if(lessonNoPy>pyLessonsData.length){
            $(".python .startLessonZero").css("display","none")
            $(".python .finishLesson").css("display","none")
            $(".python .last_lesson").css("display","none")
            $(".python .current_lesson").css("display","none")
            $(".python .next_lesson").css("display","none")
            $(".python #line1").css("display","none")
            $(".python #line2").css("display","none")
            $(".python .doPracticePy").css("display","none")
            $(".python .finishSomething").css("display","flex")
            $(".python #pyPhone").css("display","none")
        }else{
            if((lessonNoPy-1) === 0){
            document.querySelector(".python .startLessonZero").style.display = "flex"
            document.querySelector(".python .last_lesson").style.display = "none"

            DOC_currentPy.textContent = pyLessonsData[lessonNoPy-1]["lessonContent"]
            DOC_numberCurrentPy.textContent = pyLessonsData[lessonNoPy-1]["lessonID"]

            Phone_currentPy.textContent = pyLessonsData[lessonNoPy-1]["lessonContent"]
            Phone_currentPy.textContent = pyLessonsData[lessonNoPy-1]["lessonContent"]

            DOC_nextPy.textContent = pyLessonsData[lessonNoPy]["lessonContent"]
            DOC_numberNextPy.textContent = pyLessonsData[lessonNoPy]["lessonID"]
        }else{
            DOC_lastPy.textContent = pyLessonsData[lessonNoPy-2]["lessonContent"]
            DOC_numberLastPy.textContent = pyLessonsData[lessonNoPy-2]["lessonID"]

            DOC_currentPy.textContent = pyLessonsData[lessonNoPy-1]["lessonContent"]
            DOC_numberCurrentPy.textContent =pyLessonsData[lessonNoPy-1]["lessonID"]

            Phone_currentPy.textContent = pyLessonsData[lessonNoPy-1]["lessonContent"]
            Phone_currentPy.textContent = pyLessonsData[lessonNoPy-1]["lessonContent"]

            if(pyLessonsData.length == lessonNoPy){
                document.querySelector(".python .finishLesson").style.display = "flex"
                document.querySelector(".python  .next_lesson").style.display = "none"
            }else{
                DOC_nextPy.textContent = mathLessonsData[lessonNoPy]["lessonContent"]
                DOC_numberNextPy.textContent = mathLessonsData[lessonNoPy]["lessonID"]
            }
            
        }
        }
