    <script>
        //newDataTransfer

        $("#buttonChange").on("click",()=>{
            let ourEmail = $("#email").text()
            let nameSurname = $(".name").val()
            let eduLevel = $("#education-level").attr("value")
            let ourProvince = $("#province").attr("value")
            let ourSchool = $("#school").attr("value")
            
            $.ajax({
                type:"POST",
                url:"./newDataTransferSetting.php",
                data: {
                    "ourEmail":ourEmail,
                    "nameSurname":nameSurname,
                    "eduLevel":eduLevel,
                    "ourProvince":ourProvince,
                    "ourSchool":ourSchool
                },
                dataType: "json",
                success: function(data){
                    if(data){
                        console.info("CONNECTED RESULT")
                    }else{
                        console.error("NOT DEFINED RESULT !!!");
                    }
                }
            })
            window.location.href = "./personal_home.php"
        })

        $(".savePassword").on("click",()=>{
            let newPassword = $(".newPassword").val()
            let ourEmail = $("#email").text()
            if(newPassword == ''){
                $(".NewPassPlace").css("border","1px solid rgba(234,64,53,1)")
            }else{
                $.ajax({
                    type:"POST",
                    url:"./newPassword.php",
                    data: {
                        "ourEmail":ourEmail,
                        "newPass":newPassword
                    },
                    dataType: "json",
                    success: function(data){
                        if(data){
                            console.info("CONNECTED RESULT")
                        }else{
                            console.error("NOT DEFINED RESULT !!!");
                        }
                    }
                })
                $(".repeatPasswordPopup").css("display","none")
            }
        })
    </script>

