        })
        $(".signOutForm").on("click",(e)=>{
            var cookies = document.cookie.split(";")
            const DeleteEmailValue = cookies[0].split("=")[1];
            const DeletePassValue = cookies[1].split("=")[1];
            document.cookie = `email = ${DeleteEmailValue}; expires = Mon, 1 Jan 2000 00:00:00 GMT`
            document.cookie = `password = ${DeletePassValue}; expires = Mon, 1 Jan 2000 00:00:00 GMT`
        })
