   <script type="module" src="../js/headerTheme.js"></script>
    <script type="module">
        import {encryption} from '../js/encrypt.js';
        import {Decryption} from '../js/decrypt.js';

        $(".temaMode").on("click",()=>{
            themeMode()
        })

        let theme = Decryption(localStorage.getItem("theme"))
        if(theme=="darkTheme"){
            $("#wifiConnection").attr("src","../img/onlineWhite.svg")
            $("#headSetting").attr("fill","white")
            $("#tema").attr("fill","white")
            $("#date").attr("fill","white")
            $("#closeThis").attr("fill","white")
        }

        let themeMode = () =>{
            if(document.documentElement.getAttribute("data-theme") == null || document.documentElement.getAttribute("data-theme") == "light"){
                document.documentElement.setAttribute('data-theme', 'dark')
                $("#arrowSvgColor").attr("fill","white")

                $("#mainpage").attr("stroke","white")
                $("#setting").attr("fill","white")
                $("#nots").attr("fill","white")
                $("#order").attr("fill","white")
                $("#theme").attr("fill","white")
                $("#wifiConnection").attr("src","../img/onlineWhite.svg")
                
                $("#headSetting").attr("fill","white")
                $("#tema").attr("fill","white")
                $("#date").attr("fill","white")

                $("#closeThis").attr("fill","white")
                themeLocal("darkTheme")
            }else{
                document.documentElement.setAttribute('data-theme', 'light')
                $("#arrowSvgColor").attr("fill","black")

                $("#mainpage").attr("stroke","black")
                $("#setting").attr("fill","black")
                $("#nots").attr("fill","black")
                $("#order").attr("fill","black")
                $("#theme").attr("fill","black")

                $("#wifiConnection").attr("src","../img/online.svg")
                
                $("#headSetting").attr("fill","black")
                $("#tema").attr("fill","black")
                $("#date").attr("fill","black")

                $("#closeThis").attr("fill","black")
                themeLocal("lightTheme")
            }
        }
        let themeLocal = (theme)=>{
            let themeEnc = encryption(theme);
            localStorage.setItem("theme",themeEnc)
        }
    </script>

